using SMIS.Test.Utilities;

namespace SMIS.Test.Examples;

/// <summary>
/// Examples demonstrating how to use ProductFixtureBuilder with method chaining
/// </summary>
public class ProductFixtureBuilderExamples
{
    // Example 1: Basic usage with auto-generated data
    public void Example_AutoGenerated()
    {
        var product = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .Build();
    }

    // Example 2: Override specific properties
    public void Example_CustomName()
    {
        var product = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithName("Custom Product Name")
            .Build();
    }

    // Example 3: Set multiple custom properties
    public void Example_MultipleCustomProperties()
    {
        var product = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithName("Premium Laptop")
            .WithDescription("High-end gaming laptop")
            .WithSKU("LAPTOP-PREMIUM-001")
            .WithBarcode("9876543210123")
            .WithImageUrl("https://example.com/laptop.jpg")
            .WithIsActive(true)
            .Build();
    }

    // Example 4: Null description
    public void Example_NullDescription()
    {
        var product = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithDescription(null)
            .Build();
    }

    // Example 5: Inactive product
    public void Example_InactiveProduct()
    {
        var product = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithIsActive(false)
            .Build();
    }

    // Example 6: Duplicate SKU testing
    public void Example_DuplicateSKU()
    {
        var sku = "DUPLICATE-SKU-001";
        
        var product1 = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithSKU(sku)
            .Build();

        var product2 = new ProductFixtureBuilder()
            .WithDependencies("shop-id", "unit-id", "category-id")
            .WithSKU(sku)
            .Build();
    }
}
